-- | CTWritingDirection
--
-- These constants specify the writing direction
--
-- kCTWritingDirectionNatural
--
-- The writing direction is algorithmically determined                using the Unicode Bidirectional Algorithm rules P2 and P3.
--
-- kCTWritingDirectionLeftToRight
--
-- The writing direction is left to right.
--
-- kCTWritingDirectionRightToLeft
--
-- The writing direction is right to left.
-- | @CTWritingDirection@
newtype CTWritingDirection = CTWritingDirection CSChar
  deriving stock (Eq, Ord, Show)
  deriving newtype (Storable)

pattern KCTWritingDirectionNatural :: CTWritingDirection
pattern KCTWritingDirectionNatural = CTWritingDirection (-1)

pattern KCTWritingDirectionLeftToRight :: CTWritingDirection
pattern KCTWritingDirectionLeftToRight = CTWritingDirection 0

pattern KCTWritingDirectionRightToLeft :: CTWritingDirection
pattern KCTWritingDirectionRightToLeft = CTWritingDirection 1
